//清空

function cardclear(){

var AICD=document.getElementById('ADVALLDIVTOP')
var childs=AICD.childNodes
for(var i = childs.length - 1; i >= 0; i--) {  
    AICD.removeChild(childs[i]);  }  

 var cardclear= CardRANK_N.concat(CardRANK_R).concat(CardRANK_SR).concat(CardRANK_SSR).concat(CardRANK_LR).concat(CardRANK_EX).concat(CardRANK_RANK);

cardclear.forEach(function(CardID) {

try{

   document.getElementById("div"+CardID).style.position="absolute";
   document.getElementById("div"+CardID).style.left="-2000px";
   document.getElementById("div"+CardID).style.filter="brightness(100%)";

}catch(e){}

});

//alert('121')

}



